import React from 'react';
import { connect } from 'react-redux';
import { Link, RouteComponentProps } from 'react-router-dom';
import { Button, Row, Col, Label } from 'reactstrap';
import { AvForm, AvGroup, AvInput } from 'availity-reactstrap-validation';
// tslint:disable-next-line:no-unused-variable
import { Translate, translate, ICrudGetAction, ICrudGetAllAction, ICrudPutAction } from 'react-jhipster';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { IRootState } from 'app/shared/reducers';

import { IPlaneacionTrimestre } from 'app/shared/model/planeacion-trimestre.model';
import { getEntities as getPlaneacionTrimestres } from 'app/entities/planeacion-trimestre/planeacion-trimestre.reducer';
import { IActividadProyecto } from 'app/shared/model/actividad-proyecto.model';
import { getEntities as getActividadProyectos } from 'app/entities/actividad-proyecto/actividad-proyecto.reducer';
import { getEntity, updateEntity, createEntity, reset } from './planeacion-actividad.reducer';
import { IPlaneacionActividad } from 'app/shared/model/planeacion-actividad.model';
// tslint:disable-next-line:no-unused-variable
import { convertDateTimeFromServer, convertDateTimeToServer } from 'app/shared/util/date-utils';
import { mapIdList } from 'app/shared/util/entity-utils';

export interface IPlaneacionActividadUpdateProps extends StateProps, DispatchProps, RouteComponentProps<{ id: string }> {}

export interface IPlaneacionActividadUpdateState {
  isNew: boolean;
  planeacionTrimestreId: string;
  actividadProyectoId: string;
}

export class PlaneacionActividadUpdate extends React.Component<IPlaneacionActividadUpdateProps, IPlaneacionActividadUpdateState> {
  constructor(props) {
    super(props);
    this.state = {
      planeacionTrimestreId: '0',
      actividadProyectoId: '0',
      isNew: !this.props.match.params || !this.props.match.params.id
    };
  }

  componentWillUpdate(nextProps, nextState) {
    if (nextProps.updateSuccess !== this.props.updateSuccess && nextProps.updateSuccess) {
      this.handleClose();
    }
  }

  componentDidMount() {
    if (this.state.isNew) {
      this.props.reset();
    } else {
      this.props.getEntity(this.props.match.params.id);
    }

    this.props.getPlaneacionTrimestres();
    this.props.getActividadProyectos();
  }

  saveEntity = (event, errors, values) => {
    if (errors.length === 0) {
      const { planeacionActividadEntity } = this.props;
      const entity = {
        ...planeacionActividadEntity,
        ...values
      };

      if (this.state.isNew) {
        this.props.createEntity(entity);
      } else {
        this.props.updateEntity(entity);
      }
    }
  };

  handleClose = () => {
    this.props.history.push('/entity/planeacion-actividad');
  };

  render() {
    const { planeacionActividadEntity, planeacionTrimestres, actividadProyectos, loading, updating } = this.props;
    const { isNew } = this.state;

    return (
      <div>
        <Row className="justify-content-center">
          <Col md="8">
            <h2 id="ghostceetApp.planeacionActividad.home.createOrEditLabel">
              <Translate contentKey="ghostceetApp.planeacionActividad.home.createOrEditLabel">
                Create or edit a PlaneacionActividad
              </Translate>
            </h2>
          </Col>
        </Row>
        <Row className="justify-content-center">
          <Col md="8">
            {loading ? (
              <p>Loading...</p>
            ) : (
              <AvForm model={isNew ? {} : planeacionActividadEntity} onSubmit={this.saveEntity}>
                {!isNew ? (
                  <AvGroup>
                    <Label for="id">
                      <Translate contentKey="global.field.id">ID</Translate>
                    </Label>
                    <AvInput id="planeacion-actividad-id" type="text" className="form-control" name="id" required readOnly />
                  </AvGroup>
                ) : null}
                <AvGroup>
                  <Label for="planeacionTrimestre.id">
                    <Translate contentKey="ghostceetApp.planeacionActividad.planeacionTrimestre">Planeacion Trimestre</Translate>
                  </Label>
                  <AvInput
                    id="planeacion-actividad-planeacionTrimestre"
                    type="select"
                    className="form-control"
                    name="planeacionTrimestre.id"
                    value={isNew ? planeacionTrimestres[0] && planeacionTrimestres[0].id : planeacionActividadEntity.planeacionTrimestre.id}
                  >
                    {planeacionTrimestres
                      ? planeacionTrimestres.map(otherEntity => (
                          <option value={otherEntity.id} key={otherEntity.id}>
                            {otherEntity.id}
                          </option>
                        ))
                      : null}
                  </AvInput>
                </AvGroup>
                <AvGroup>
                  <Label for="actividadProyecto.id">
                    <Translate contentKey="ghostceetApp.planeacionActividad.actividadProyecto">Actividad Proyecto</Translate>
                  </Label>
                  <AvInput
                    id="planeacion-actividad-actividadProyecto"
                    type="select"
                    className="form-control"
                    name="actividadProyecto.id"
                    value={isNew ? actividadProyectos[0] && actividadProyectos[0].id : planeacionActividadEntity.actividadProyecto.id}
                  >
                    {actividadProyectos
                      ? actividadProyectos.map(otherEntity => (
                          <option value={otherEntity.id} key={otherEntity.id}>
                            {otherEntity.id}
                          </option>
                        ))
                      : null}
                  </AvInput>
                </AvGroup>
                <Button tag={Link} id="cancel-save" to="/entity/planeacion-actividad" replace color="info">
                  <FontAwesomeIcon icon="arrow-left" />
                  &nbsp;
                  <span className="d-none d-md-inline">
                    <Translate contentKey="entity.action.back">Back</Translate>
                  </span>
                </Button>
                &nbsp;
                <Button color="primary" id="save-entity" type="submit" disabled={updating}>
                  <FontAwesomeIcon icon="save" />
                  &nbsp;
                  <Translate contentKey="entity.action.save">Save</Translate>
                </Button>
              </AvForm>
            )}
          </Col>
        </Row>
      </div>
    );
  }
}

const mapStateToProps = (storeState: IRootState) => ({
  planeacionTrimestres: storeState.planeacionTrimestre.entities,
  actividadProyectos: storeState.actividadProyecto.entities,
  planeacionActividadEntity: storeState.planeacionActividad.entity,
  loading: storeState.planeacionActividad.loading,
  updating: storeState.planeacionActividad.updating,
  updateSuccess: storeState.planeacionActividad.updateSuccess
});

const mapDispatchToProps = {
  getPlaneacionTrimestres,
  getActividadProyectos,
  getEntity,
  updateEntity,
  createEntity,
  reset
};

type StateProps = ReturnType<typeof mapStateToProps>;
type DispatchProps = typeof mapDispatchToProps;

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(PlaneacionActividadUpdate);
